type storageInfo struct {
	Name       string
	Size       uint64
	FreeSpace  uint64
}
var storageinfo []storageInfo

func main() {
	//查系统磁盘剩余可用空间
	wmi.Query("Select * from Win32_LogicalDisk",  &storageinfo)
	//创建窗口
	w, err := window.New(sciter.DefaultWindowCreateFlag, &sciter.Rect{0, 0, 728, 624})
	if err != nil {
		log.Fatal(err)
	}
	w.LoadHtml(html,"")
	root, _ := w.GetRootElement();

	initDownSize(root)
	handlerClickInstall(w, root)
	handleStopInstall(root)
	handlerOpenUrl(root)
	handlerOpenAds(w)

	w.Show(); //显示窗口
	w.Run(); //运行窗口，进入消息循环
}


//检查磁盘可用空间大小
func initDownSize(root *sciter.Element)  {
	getDownUrl(func(durl string) {
		_, err := url.ParseRequestURI(durl)
		if err != nil {
			log.Println("网址错误")
		}
		client := new(http.Client)
		resp, _ := client.Get(durl)
		fsize := int(resp.ContentLength) * 2 //服务器返回的文件大小

		arrLen := len(storageinfo)
		var disk string
		for i := arrLen - 1; i >= 0; i-- {
			if int(storageinfo[i].FreeSpace) > fsize {
				fmt.Println(storageinfo[i].Name, "==", int(storageinfo[i].FreeSpace))
				disk = storageinfo[i].Name
				break
			}
		}
		//for k, storage := range storageinfo {
		//	fmt.Println(k, storage)
		//	if int(storage.FreeSpace) > fsize {
		//		//fmt.Println(storage.Name, "==", int(storage.FreeSpace))
		//		disk = storage.Name
		//		break
		//	}
		//}
		//初始化已经下载的资源文件
		spath, _ := root.SelectById("folderPath")
		savePath := disk + "/elsDown"
		spath.SetValue(sciter.NewValue(savePath))

		downSize, totalSize := getInitFileSize(durl, savePath)
		percentage := strconv.Itoa((downSize * 100) / totalSize)
		speed, _ := root.SelectById("speedValue")
		strong, _ := root.SelectById("totalVal")
		go speed.SetStyle("width", percentage + "%")
		go speed.SetHtml("<span>"+ percentage +"%</span>", sciter.SIH_REPLACE_CONTENT)
		go strong.SetHtml("<span>"+ bytesToSize(downSize) +"/" + bytesToSize(totalSize) +"</span>", sciter.SIH_REPLACE_CONTENT);

	})

}

func isDirExists(path string) bool {
	fi, err := os.Stat(path)
	if err != nil {
		return os.IsExist(err)
	} else {
		return fi.IsDir()
	}
}
//获取已经下载文件大小
func getInitFileSize(durl, savePath string) (downSize, totalSize int) {
	uri, err := url.ParseRequestURI(durl)
	if err != nil {
		log.Println("网址错误")
	}
	filename := path.Base(uri.Path)
	//检查是否有目录，没有则创建
	if !isDirExists(savePath) {
		os.Mkdir(savePath, 0666)
	}
	//O_RDWR|O_CREATE是文件不存在的情况下就建一个空文件
	f, err := os.OpenFile(savePath + "/" + filename, os.O_RDWR|os.O_CREATE, 0666)
	if err != nil {
		log.Fatalln(err)
	}
	defer f.Close()
	stat, err := f.Stat() //获取文件状态
	if err != nil {
		log.Fatalln(err)
	}
	client := new(http.Client)  	//创建一个http client
	resp, err := client.Get(durl)   //get方法获取资源
	if err != nil {
		log.Fatalln(err)
	}
	fsize := int(resp.ContentLength) //服务器返回的文件大小

	return int(stat.Size()), fsize
}


//处理点击安装包
func handlerClickInstall(w *window.Window, root *sciter.Element) {
	spath, _ := root.SelectById("folderPath")
	dpath,_ := spath.GetValue()
	savePath := dpath.String()
	//点击开始安装
	w.DefineFunction("installPack", func(args ...*sciter.Value) *sciter.Value {
		ret := sciter.NewValue()
		speed, _ := root.SelectById("speedValue")
		strong, _ := root.SelectById("totalVal")
		btn, _ := root.SelectById("btnInstall")
		stopBtn, _ := root.SelectById("stopBtn")

		btn.SetStyle("display","none")
		stopBtn.SetStyle("display","block")

		//获取资源下载 url地址
		getDownUrl(func(durl string) {
			state = "on"
			//得到包的已下载的大小和总大小
			downSize, totalSize := getInitFileSize(durl, savePath)

			//处理下载网络包资源
			go downloadFile(durl, savePath ,func(downLen int, fname string) {
				writeTotal := downLen + downSize
				fileSize := bytesToSize(totalSize)   //包总大小
				writeSize := bytesToSize(writeTotal)  //已写的大小
				percentage := strconv.Itoa((writeTotal * 100) / totalSize)
				//操作html元素
				go speed.SetStyle("width", percentage + "%")
				go speed.SetHtml("<span>"+ percentage +"%</span>", sciter.SIH_REPLACE_CONTENT)
				go strong.SetHtml("<span>"+ writeSize +"/" + fileSize +"</span>", sciter.SIH_REPLACE_CONTENT);

				if fname != "null" {
					log.Println("下载完成")
					go strong.SetHtml("<span>解压中</span>", sciter.SIH_REPLACE_CONTENT)
					go speed.SetStyle("width", "0%")
					go speed.SetHtml("0%", sciter.SIH_REPLACE_CONTENT)
					stopBtn.SetAttr("disabled", "disabled")
					//去掉文件名后缀，得到一个解压目录
					dname := strings.TrimSuffix(fname, path.Ext(fname))
					//开始解压
					unRarPack(dname, fname, savePath, func(num int, res string) {
						if res == "ok" {
							log.Println("解压完成")
							//启动游戏
							cmd := exec.Command(savePath + "/" + dname + "/" + "elsword.exe")
							if err := cmd.Start(); err != nil {
								log.Println(err)
							}
							windows.Exit(0) //退出窗口程序
						}
						percenRar := strconv.Itoa((num * 100) / totalSize)
						//log.Println(percenRar)
						go speed.SetStyle("width", percenRar + "%")
						go speed.SetHtml("<span>"+ percenRar +"%</span>", sciter.SIH_REPLACE_CONTENT)

					})
				}
			})

		})

		return ret
	})
}

var state = "on"

func handleStopInstall(root *sciter.Element)  {
	stopBtn, _ := root.SelectById("stopBtn")
	sbtn,_ := root.SelectById("stopBtn")
	btn, _ := root.SelectById("btnInstall")
	sbtn.OnClick(func() {
		btn.SetStyle("display","block")
		stopBtn.SetStyle("display","none")
		state = "off"
	})
}
//下载文件包
func downloadFile(durl, savePath string, fb func(downLen int, comp string)) error {
	uri, err := url.ParseRequestURI(durl)
	if err != nil {
		log.Println("网址错误")
	}
	filename := path.Base(uri.Path)
	//log.Println("[*] Filename " + filename)
	//O_RDWR|O_CREATE是文件不存在的情况下就建一个空文件
	f, err := os.OpenFile(savePath + "/" + filename, os.O_RDWR|os.O_CREATE, 0666)
	if err != nil {
		log.Fatalln(err)
	}
	defer f.Close()
	stat, err := f.Stat() //获取文件状态
	if err != nil {
		log.Fatalln(err)
	}
	//把文件指针指到文件末
	f.Seek(stat.Size(), 0)

	var req http.Request
	req.Method = "GET"
	req.Close = true
	req.URL, _ = url.Parse(durl)
	header := http.Header{}
	//每次打开文件将指针指向末尾，然后将当前文件大小做为开始字节，下载剩下部分
	header.Set("Range", "bytes="+strconv.FormatInt(stat.Size(), 10)+"-")
	req.Header = header
	resp, err := http.DefaultClient.Do(&req)
	if err != nil {
		return err
	}
	raw := resp.Body
	defer raw.Close()

	reader := bufio.NewReaderSize(raw, 1024*32);
	writer := bufio.NewWriter(f)
	buff := make([]byte, 32*1024)
	written := 0

	for {
		if state == "off" {
			break
		} else {
			//读取bytes
			nr, er := reader.Read(buff)
			if nr > 0 {
				//写入bytes
				nw, ew := writer.Write(buff[0:nr])
				//数据长度大于0
				if nw > 0 {
					written += nw
				}
				//写入出错
				if ew != nil {
					err = ew
					break
				}
				//读取是数据长度不等于写入的数据长度
				if nr != nw {
					err = io.ErrShortWrite
					break
				}
			}
			if er != nil {
				if er != io.EOF {
					err = er
				}
				//下载完成
				fb(written, filename)
				break
			}
			//没有错误了快使用 callback
			go fb(written,"null")
		}
	}
	return err
}



//获取艾尔之光免安装包下载地址
func getDownUrl(eurl func(e string))  {
	res, _ := http.Get("下载页面")
	defer res.Body.Close()

	doc, _ := goquery.NewDocumentFromReader(res.Body)
	doc.Find("a[href*='抓取下载地址']").Each(func(i int, selection *goquery.Selection) {
		if selection.Text() == "免安装版" {
			rs, rh := selection.Attr("href")
			if rh == true {
				eurl(rs)
			}
		}
	})
}


//处理解压
func unRarPack(dname, fname, savePath string, ret func(num int, res string))  {
	r := archiver.Rar{
		OverwriteExisting: 		true,
		ContinueOnError: 		false,
	}
	c := cron.New()
	c.Start()
	c.AddFunc("*/3 * * * * *", func() {
		var num int64
		num = 0
		filepath.Walk(savePath + "/" + dname, func(path string, info os.FileInfo, err error) error {
			//log.Println(info.Name())
			num += info.Size()
			return nil
		})
		go ret(int(num), "now")
	})

	log.Println("start unrar")
	r.Unarchive(savePath + "/" + fname, savePath + "/" + dname)
	log.Println("end unrar")
	c.Stop()

	ret(0, "ok")
}

//字节转换单位
func bytesToSize(length int) string {
	var k = 1024 // or 1024
	var sizes = []string{"Bytes", "KB", "MB", "GB", "TB"}
	if length == 0 {
		return "0 Bytes"
	}
	i := math.Floor(math.Log(float64(length)) / math.Log(float64(k)))
	r := float64(length) / math.Pow(float64(k), i)
	return strconv.FormatFloat(r, 'f', 3, 64) + " " + sizes[int(i)]
}

